import React from 'react'
import styled, { css } from 'styled-components'
export default function button({
  children,
  type = '',
  onClick = () => {},
  className = '',
  loading = false,
  disabled = false,
}) {
  const Btn = styled.a`
    padding: 3px 7px;
    border: 1px solid #d9d9d9;
    border-radius: 2px;
    cursor: pointer;
    box-shadow: 0 2px transparent;
    ${(props) =>
      (props.secondary &&
        css`
          background: #6c757d;
          color: white;
        `) ||
      (props.success &&
        css`
          background: #28a745;
          color: white;
        `) ||
      (props.danger &&
        css`
          background: #dc3545;
          bgloading: rgb(210, 82, 94);
          color: white;
        `) ||
      (props.primary &&
        css`
          background: #d62121;
          color: #ad6666;
        `) ||
      (props.warning &&
        css`
          background: #ffc107;
          color: #212529;
        `) ||
      (props.info &&
        css`
          background: #12a3ba;
          color: 'white';
        `) ||
      (props.light &&
        css`
          bacground: #f8f9fa;
          color: #212529;
        `) ||
      (props.dark &&
        css`
          bacground: #343a40;
          color: white;
        ``
      `)}
  `
  return (
    <div>
      <Btn
        disabled={disabled}
        className={className}
        onClick={onClick}
        className={className}
      >
        <span className={'content_btn'}>
          {loading && <div className={'loading'}></div>}
          <span>{children}</span>
        </span>
      </Btn>
      {/* <Btn
        className={className}
        onClick={onClick}
        className={`'button_default' ${className}`}
        style={{
          backgroundColor: '#fff',
          color: '#212529',
        }}
      >
        {children}
      </Btn> */}
    </div>
  )
}
