import React from 'react'
import styled, { css } from 'styled-components'
export default function Button({
  children,
  type = '',
  onClick = () => {},
  className = '',
  loading = false,
  disabled = false,
}) {
  const types = [
    { type: 'primary', bgColor: '#007bff', color: 'white' },
    { type: 'secondary', bgColor: '#6c757d', color: 'white' },
    { type: 'success', bgColor: '#28a745', color: 'white' },
    {
      type: 'danger',
      bgColor: '#dc3545',
      bgLoading: 'rgb(210, 82, 94)',
      color: 'white',
    },
    { type: 'warning', bgColor: '#ffc107', color: '#212529' },
    { type: 'info', bgColor: '#12A3BA', color: 'white' },
    { type: 'light', bgColor: '#f8f9fa', color: '#212529' },
    { type: 'dark', bgColor: '#343a40', color: 'white' },
  ]
  const Button = styled.a`
    padding: 3px 7px;
    border: 1px solid transparent;
    border-radius: 2px;
    cursor: pointer;
    box-shadow: 0 2px transparent;
    ${(props) =>
      props.primary &&
      css`
        background: white;
        color: black;
      `}
  `
  const Loading = {
    width: '15px',
    height: '15px',
    borderRadius: '50%',
    display: 'inline-block',
    borderTop: '2px solid #fff',
    borderRight: '2px solid transparent',
    boxSizing: 'border-box',
    animation: 'rotation 1s linear infinite',
    padding: '0 3px',
  }
  const content_btn = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }
  const button = {
    padding: '3px 7px',
    border: '1px solid transparent',
    borderRadius: '2px',
    cursor: 'pointer',
    boxShadow: '0 2px transparent',
  }
  return (
    <div>
      {type ? (
        types.map((i) => {
          if (type == i.type) {
            return (
              <Button
                disabled={disabled}
                className={className}
                onClick={onClick}
                style={{ button }}
              >
                <span className={content_btn}>
                  {loading && <div style={Loading}></div>}
                  <span>{children}</span>
                </span>
              </Button>
            )
          }
        })
      ) : (
        <Button className={className} onClick={onClick}>
          {children}
        </Button>
      )}
    </div>
  )
}
